import { RECEIVE_BEERS } from '../actions/beers'
import { RECEIVE_DATA} from '../actions/retrieveAllData'

export default function beers (state = {}, action) {
    console.log('Action Type', action.type)

    switch (action.type) {
        case RECEIVE_BEERS :
            console.log('Receiving beers')
            return {
                ...state,
                ...action.beers
            }
        case RECEIVE_DATA :
            return {
                ...state,
                ...action.beers
            }
        default :
            return state
    }
}
