import React from 'react';
import './VoteButton.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { Vote } from '../actions/ratings/vote';

export function VoteButton(props) {
    return (
        <button
            className={props.vote === true ? 'btn_up_vote' : 'btn'}
            onClick={props.vote({id: 1})}
        >
            Vote
        </button>
  );
}

const mapStateToProps = () => {

    return {
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        vote: (data) => { dispatch(Vote(data)) }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(VoteButton)
