const api = "https://rebooze.herokuapp.com"

var obj = {
    mode: 'cors',
    headers: {
      'Content-Type': 'application/json'
    }
  }

export const getBeers = () =>
    fetch(`${api}/beers`, obj)
    .then(res => res.json())

export const submitRating = () =>
fetch(`${api}/rating`, {
  method: 'POST',
  headers: {
    ...obj.headers,
  },
  body: JSON.stringify({

    'BeerID': 1,
    'UserID': 1,
    'Rating': 4.2,
    'comment': "Beer was nice"

  })
})
