import React, { Component } from 'react'
import { connect } from 'react-redux';
import BeerListItem from './BeerListItem'
import retieveRatingsAsync from '../actions/beers/retrieveBeersAsync'


class Beer extends Component {

    componentDidMount () {
        const ratings = this.props.retrieveRatings(1)
        console.log("The ratings are")
        console.log(ratings)
    }

    render () {




        console.log("Beers")
        console.log(this.props.beerSelected)
        const beer = this.props.beers && this.props.beers.filter((c) => (
            c.name === this.props.beerSelected
        ))
        console.log(beer)

        return (
            <div>
            <div>
                <h1>Beer Page</h1>
            </div>
            <div>
                <h1>{beer && beer[0].name}</h1>
            </div>
            <div>
                <h1>Your Rating</h1>
            </div>
            <div>
                <h1>Top Ratings</h1>
            </div>
            </div>

        )

    }


}

const mapStateToProps = ({beers}) => {

    return {
        beers: beers.beers,
        beerSelected: beers.beerSelected
    }

}

const mapDispatchToProps = (dispatch) => {
    return {
        retrieveRatings: (data) => {dispatch(retieveRatingsAsync(data))}     
    }
  }

export default connect(mapStateToProps, mapDispatchToProps)(Beer)
