import React, { Component } from 'react'
import { connect } from 'react-redux';
import beerSearch from '../actions/beerSearch'

class BeerList extends Component {

    componentDidMount () {
        this.props.beerSearch("White")
    }
    render () {

        const { beers } = this.props
        console.log('Beer list')
        console.log(beers)


        return (
            <div>
                <div>
                    <input
                        type='text'
                        value={this.props.searchTerm}
                        placeholder='Search Beers'
                        onChange={(event) => this.props.beerSearch(event.target.value)}
                    />
                </div>
                <div>
                    <ol>
                    {this.props.beers && this.props.beers.map((beer) => (
                        <li key = {beer.id}>
                            {beer.name}
                            {beer.id}
                        </li>
                        )
                        )}
                    </ol>
                </div>
            </div>




        )}

}

const mapStateToProps = ({beers}) => {

    return {
        beers: beers.beers,
        searchTerm: beers.searchTerm

    }

}

const mapDispatchToProps = (dispatch) => {
    return {
      beerSearch: (data) => {dispatch(beerSearch(data))}
    }
  }

export default connect(mapStateToProps, mapDispatchToProps)(BeerList)
