import React, { Component, useEffect, useState } from 'react'
import { connect } from 'react-redux';
import retrieveUserDataAsync from '../actions/users/retrieveUserDataAsync';
import { useAuth0, auth0 } from "@auth0/auth0-react";
import Cookies from 'universal-cookie';



const Callback = (props) => {

    const { user, isAuthenticated, getAccessTokenSilently } = useAuth0()
    const [posts, setPosts] = useState(null);
    console.log("The user is")
    console.log(isAuthenticated)


    const cookies = new Cookies();
    cookies.set('myCat', 'Pacman', { path: '/' });
    console.log("The cookie is :")
    console.log(cookies.get('myCat')); // Pacman


    typeof user !== 'string' && isAuthenticated && props.retrieveUserData({
        "name": user.given_name,
        "email": user.email
    })

    

    useEffect(() => {
        (async () => {
            try {
                const token = await getAccessTokenSilently();
                console.log("the token is:")
                console.log(token)
                setPosts();
            } catch (e) {
                console.error(e);
            }
        })();
    }, [getAccessTokenSilently]);

    return (
        <div>
            Callback Function
        </div>
    )
}

const mapStateToProps = ({user}) => {

    return {
      user: user
    }
  }
  
  
  const mapDispatchToProps = (dispatch) => {
    return {
      retrieveUserData: (data) => {dispatch(retrieveUserDataAsync(data))}
    }
  }

export default connect(mapStateToProps, mapDispatchToProps)(Callback)
